var entries = str.split("|", -1);

var size = entries.length;
let table = [];

let addresses = [];

let info = [];

function initMap() {

    geocoder = new google.maps.Geocoder();

    // map params
    let options = {
        zoom: 4.5,
        center: {lat:39.8283459, lng:-98.5794797} //center of the United States
    }
    // The map object
    let map = new google.maps.Map(
        document.getElementById('map'), options);

        entries.forEach(sortFunction);

        table.forEach(extractAddressFunction);

        table.forEach(infoFunction);

        addresses.forEach(codeAddress);
}

function sortFunction(item) {
  table.push(item.split("~", -1));
}

function extractAddressFunction(item, index) {
	addresses.push(item[2]);
  addresses[index] = addresses[index] + ", " + item[0];
}

function infoFunction(item) {
  var infoStr = "<h2>" + item[1] + "</h2>" + "<p>State: " + item[0] + "</p>" + "<p>Address: " + item[2] + "</p>" + "<p>City: " + item[3] + "</p>" + "<p>Facility: " + item[4] + "</p>" + "<p>Hours: " + item[5] + "</p>" + "<p>Phone: " + item[6] + "</p>" + "<p>Eligibility: " + item[7] + "</p>" + "<p>Link: " + item[8] + "</p>";
  info.push(infoStr);
}

function codeAddress(address, index)
{
  geocoder.geocode( {address:address}, function(results, status)
  {
    if (status == google.maps.GeocoderStatus.OK)
    {
      var marker = new google.maps.Marker(
      {
          map: map,
          position: results[0].geometry.location
      });

      let infoWindow = new google.maps.InfoWindow({
          content: info[index],
      });

      marker.addListener('click', () => {
          infoWindow.open(map, marker);
      });

    }
  });
}
